 */
class Activity extends BaseActivity
{
  function __toString()
  {
    return $this->getName();
  }
  
  public function generateImageFilename(sfValidatedFile $file)
  {
    $dir = sfConfig::get('sf_upload_dir') . DIRECTORY_SEPARATOR. 'activity' .DIRECTORY_SEPARATOR;
    $originalFile = str_replace($file->getOriginalExtension(), '', $file->getOriginalName());
    $destFile = $originalFile.'_act'.$file->getOriginalExtension();
    if (!file_exists($dir.$destFile))
      return $destFile;
    $nb = 1;
    while (file_exists($dir.$destFile)) {
        $destFile = $originalFile.'_act'.$nb.$file->getOriginalExtension();
        $nb++;
    }
    return $destFile;
  }
}